package org.example.models.generated;

import java.lang.annotation.Retention;
import java.lang.annotation.RetentionPolicy;
import java.lang.annotation.Target;
import java.lang.annotation.ElementType;

/**
 * Marker for classes generated by BlockchainEntityProcessor.
 * Holds the on-chain struct name so ModelRegistry can pick it up.
 */
@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.TYPE)
public @interface AutoGeneratedModel {
    /** the on-chain struct name (e.g. "Bar") */
    String structName();
}
